package Swapping;

public class Swap {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=20;
		int b=30;
		int temp=0;
		System.out.println("First Number---"+a+"\n Second Number----"+b);
	temp=a;
	a=b;
	b=temp;
	System.out.println("First Number---"+a+"\n Second Number----"+b);
	
	
	}

}
